engine.script:
var $dataInicio = $('#bannerDataInicio');
var $dataFim = $('#bannerDataFim'); 

$("#bannerDataInicio, #bannerDataFim").datepicker({ 
	dateFormat: "dd/mm/yy",
	dayNamesMin: [ "Dom", "Seg", "Ter", "Qua", "Qui", "Sex", "Sa" ],
	monthNames: [ "Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho", "Julho", "Agosto", "Setembro", "Outubro", "Novembro", "Dezembro" ],
	onSelect : function(data, input) {
		var dataFim = $dataFim.datepicker('getDate');
		var dataInicio = $dataInicio.datepicker('getDate');

		if (dataInicio && dataFim < dataInicio)
			$dataFim.datepicker('setDate', dataInicio)

		if (this.id == $dataInicio.attr('id'))
			$('#bannerHoraInicio').focus();
		else
			$('#bannerHoraFim').focus();
	}
});




$('input:text').setMask();
$('#new_banner [title]').tipsy({trigger: 'focus', gravity: 'w', fade: true});
$("#new_banner input[type=button]").click(dextranet.banner.novoBanner);
