engine.script:
var pessoas = [];

$('#remove').click(function() {
	var idRemover = $('#listaPessoas option:selected').val();
	pessoas.splice( pessoas.indexOf( idRemover ), 1 );
	$('#membros').val(pessoas);
	return !$('#listaPessoas option:selected').remove();
});

$(function() {
	$.ajax({
		url: "/s/usuario",
		loading: false,
		dataType: "json",
		success: function( data ) {
			var source =  $.map( data, function( item ) {
				return {
					label: item.nome,
					id: item.id
				}
			});

			$( "#pessoa" ).autocomplete({
				source: source,
				minLength: 1,
				select: function( event, ui ) {
					if(!$("#listaPessoas option[value='"+ui.item.id+"']").val()){
						$( "#listaPessoas" ).append($("<option></option>").val(ui.item.id).html(ui.item.label) );
					};
				},
				close: function( event, ui ) {
					$("#pessoa").val('');
					$("#listaPessoas>option").each(function()
					{
					    pessoas.push($(this).val());
					});
					$('#membros').val(pessoas);
				}
			});
		}
	});
});

//Lista de pessoas selecionadas

$('#btnSalvar').click(function(){
	console.info($('#membros'));
	dextranet.grupos.salvar();
});
