engine.script:

var tabs = $( "div#listaServicos" );

var qtdServico = 2;

//Add TAB serviço
function addTab(servico, id_servico) {
	conteudo = "<li class='servicoTitulo'><span id='icone' class='icon_gtalk-mini icon'></span>" + servico + "<span id='" + id_servico + "' class='ui-icon ui-icon-close novoServico excluirServico'>Remover serviço</span></li>";
	novoItem = tabs.append("<div id='"+ id_servico+ "' class='itemServico'>" + conteudo + "</div>");
	var tab = $( "<div id='tabs-" + qtdServico + "'></div>").appendTo("div#listaServicos div:last");
	qtdServico++;

		// Não será assim, claro..
		if(servico == 'Grupo email interno'){
			dextranet.grupos.listarGGrupo(id_servico, tab);
		}else{
			dextranet.grupos.listarGGrupoExterno(id_servico, tab);
		}
}

//Add servico
$('#addServico').click(function(){
	var servico = $("select#servicos").find(":selected").text();
	var id_servico = $("select#servicos").val();

	//Retirar daqui; colocar no grupo.js?!
	if (dextranet.grupos.googleGrupos.length == 0 && $.active == 0) {
		$.ajax({
			url : "/s/grupo/googlegrupos/listarGrupos",
			dataType : "json",
			success : function(data) {
				dextranet.grupos.googleGrupos = data;
			}
		});
	}

	addTab(servico, id_servico);

});

// Excluindo serviço
tabs.delegate( "span.novoServico", "click", function() {
	$( this ).closest("div.itemServico").animate({height: "0px"}, "fast", function(){
		$( this ).remove();
	});
});

//Remove pessoas selecionadas
var pessoas = [];
$('#remove').click(function() {
	var idRemover = $('#listaPessoas option:selected').val();
	pessoas.splice( pessoas.indexOf( idRemover ), 1 );
	return !$('#listaPessoas option:selected').remove();
});

//Auto Complete pessoas
$( "#pessoa" ).autocomplete({
	source: dextranet.pessoas,
	minLength: 1,
	select: function( event, ui ) {
		if(!$( "#listaPessoas option[value='"+ui.item.id+"']").val()){
			$( "#listaPessoas" ).append($("<option username = ''></option>").val(ui.item.id).html(ui.item.label) );
			$( "#listaPessoas option[value='"+ui.item.id+"']").attr("username", ui.item.username);
		};
	},
	close: function( event, ui ) {
		$("#pessoa").val('');

	}
});

//Lista de pessoas selecionadas

$('#btnSalvar').click(function(){
	var usuarios = [];
	$("#listaPessoas>option").each(function()
	{
	    nome = $(this).html();
	    id = $(this).val();
	    username = $(this).attr("username");
	    usuarios.push({ id: id, nome: nome, email: username});

	});

	//Emails Externos
	var emailGrupoExterno;
	var idServico;
	dextranet.grupos.emailsExternos = [];
	$('form.ggrupoExterno').find('input[name="grupoEmailExterno"]').each(function(){
		var emailsExternos = [];
		emailGrupoExterno = $(this).val();
		idServico = $(this).parent().find('input[name="idServico"]').val();
		$(this).parent().find('p#emailExterno').each(function(){
			emailsExternos.push($(this).attr('email'));
		});
		dextranet.grupos.emailsExternos.push({grupoEmail : emailGrupoExterno, idServico : idServico, emails : emailsExternos});
	});

	dextranet.grupos.usuariosSelecionados = usuarios;
	dextranet.grupos.salvar();
});
