template.script:


function slide_up(){

	$('li#'+'${comentario.id}').slideUp(function(){
		$(this).remove();
		$total_comentarios = $('a.' + '${postId}' + ' span.numero_comentario');
		$total_comentarios.text(parseInt($total_comentarios.text())-1);
	});
}

//Lista novo comentário
$comentario = $('li#' + '${postId}' + ' ul.list_stories_comments' + ' li.'+'${comentario.id}');
$('li.'+'${comentario.id}').remove();
$comentario.appendTo($('li#' + '${postId}' + '.clearfix ul.list_stories_comments'));

//Calcula tempo de postagem
$.each($('.time_ago'), function(){
	data_completa = $(this).html();
	tempo_atras = timeAgo.calcular(data_completa);
	$(this).html(tempo_atras);
	$(this).attr('title', data_completa);
});

//Tooltips
$('div.avatar-frame').tipsy();
$('a.showLikes').tipsy({html: true });
$('button').tipsy();
$('span.time_ago').tipsy();
